PREFIX onto: <http://www.ontotext.com/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> 
CONSTRUCT { 
    ?node ?edge ?newNodeLTR . 
    ?newNodeRTL ?edge ?node . 
} 
FROM onto:explicit
WHERE { 
    { ?node ?edge ?newNodeLTR . } 
    UNION 
    { ?newNodeRTL ?edge ?node . } 
    FILTER(?edge != rdf:type) 
}

resourceQuery:
PREFIX geo: <http://www.opengis.net/ont/geosparql#>
PREFIX cim: <https://cim.ucaiug.io/ns#>
PREFIX afn: <http://jena.apache.org/ARQ/function#>
PREFIX sesame: <http://www.openrdf.org/schema/sesame#>
select * {
    ?node sesame:directType ?type
    filter(?type != geo:Feature)
    optional {?node cim:IdentifiedObject.name ?name}
    bind(concat(afn:localname(?type),"\n",coalesce(?name,"")) as ?label)
    optional {?node cim:IdentifiedObject.description ?comment}    
}

