PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
construct {
    ?node1 rdfs:subClassOf ?node.
    ?node  rdfs:subClassOf ?node2 
}
where {
    {?node1 sesame:directSubClassOf ?node} union
    {?node  sesame:directSubClassOf ?node2}
}

