construct {
  ?node  cimr:connectedThroughPart ?node1.
  ?node2 cimr:connectedThroughPart ?node.
} where {
  values ?type {cim:Substation cim:Line}
  {?node  cimr:connectedThroughPart ?node1. ?node1 a ?type}
  union {?node2 cimr:connectedThroughPart ?node. ?node2 a ?type}
}

